    public String getNotifyId() {
        return notifyId;
    }

    public void setNotifyId(String notifyId) {
        this.notifyId = notifyId;
        if (notifyLabel != null) { 
            notifyLabel.setText(notifyId != null ? "Notification ID: " + notifyId : "Notification ID: ");
        }
    }

    public String getDate() {
        return date;
    }

    public void setDate(String date) {
        this.date = date;
        if (dateLabel != null) { 
            dateLabel.setText(date != null ? "Date: " + date: "Date: ");
        }
    }

    public String getComment() {
        return content;
    }

    public void setComment(String content) {
        this.content = content;
        if (reviewContentLabel != null) { 
            reviewContentLabel.setText(content != null ? "<html>"+content.replace("|","<br>")+"</html>" : "NULL");
        }
    }
    
    public Color getEdgeColor() {
        return edgeColor;
    }

    public void setEdgeColor(Color edgeColor) {
        this.edgeColor = edgeColor;
        if (roundedPanel.getBackground() != null) {
            roundedPanel.setBackground(edgeColor != null ? edgeColor : Color.WHITE);
        }
    }

    public Color getBackgroundColor() {
        return backgroundColor;
    }

    public void setBackgroundColor(Color backgroundColor) {
        this.backgroundColor = backgroundColor;
        if(roundedPanel.getBackgroundColor() != new Color(248,248,248)){
            roundedPanel.setBackgroundColor(backgroundColor != null ? backgroundColor : Color.WHITE);
        }
    }
    
    public JLabel getLabel(){
        return imageLabel;
    }

