    public String getReviewId() {
        return reviewId;
    }

    public void setReviewId(String reviewId) {
        this.reviewId = reviewId;
        if (idLabel != null) {
            idLabel.setText(reviewId != null ? "Review ID: " + reviewId : "Review ID: ");
        }
    }

    public String getRating() {
        return rating;
    }

    public void setRating(String rating) {
        this.rating = rating;
        if (ratingLabel != null) {
            ratingLabel.setText(rating != null ? "Rating: " + rating + "/5" : "Rating: ");
        }
    }

    public String getComment() {
        return comment;
    }

    public void setComment(String comment) {
        this.comment = comment;
        if (comment != null && !comment.isEmpty()) {
            String formattedComment = "<html>" + comment + "</html>";
            reviewContentLabel.setText(formattedComment);
        } else {
            reviewContentLabel.setText("No comments found.");
        }
    }
    
    public Color getEdgeColor() {
        return edgeColor;
    }

    public void setEdgeColor(Color edgeColor) {
        this.edgeColor = edgeColor;
        if (roundedPanel.getBackground() != null) {
            roundedPanel.setBackground(edgeColor != null ? edgeColor : Color.WHITE);
        }
    }

    public Color getBackgroundColor() {
        return backgroundColor;
    }

    public void setBackgroundColor(Color backgroundColor) {
        this.backgroundColor = backgroundColor;
        if(roundedPanel.getBackgroundColor() != new Color(248,248,248)){
            roundedPanel.setBackgroundColor(backgroundColor != null ? backgroundColor : Color.WHITE);
        }
    }
    
    public JLabel getLabel(){
        return imageLabel;
    }

